    private void passCheckboxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_passCheckboxActionPerformed
        if(passCheckbox.isSelected()){
            passwordField.setEchoChar((char)0);
        }
        else{
            passwordField.setEchoChar('*');
        }
    }//GEN-LAST:event_passCheckboxActionPerformed

    private void buttonCloseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonCloseActionPerformed
        int check = JOptionPane.showConfirmDialog(null,"Do you really want to close application?", "Select", JOptionPane.YES_NO_OPTION);
        if(check == 0){
            System.exit(0);
        }
    }//GEN-LAST:event_buttonCloseActionPerformed

    private void buttonLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonLoginActionPerformed
        String s = passwordField.getText();
        
        try{
            Connection con = dbconnect.connect();
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery("select * from userpass");
            if(rs.next()){
                String p = rs.getString(1);
                
                if(p.equals(s)){
                    setVisible(false);
                    new homepage().setVisible(true);
                }
                else{
                    JOptionPane.showMessageDialog(null, "Incorrect Password!!!");
                }
            }
            
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_buttonLoginActionPerformed

